select name as 'Service Provider Name',email as 'Email ID',serviceName as 'Service Type',serviceDescription as 'Service Description',mobile as 'Mobile Number',locality as Locality,city as City,zipcode as Zipcode from serviceprovider inner join users on serviceprovider.userid= users.id inner join services On serviceprovider.serviceid= services.id where serviceid=(select id from services where serviceName='Carpenter') AND name = ''